mapper.update:
UPDATE SUBJECT 
SET SUB_NOW_STUDENT = SUB_NOW_STUDENT + 1
WHERE SUB_SEQ=#{sub_seq}

UPDATE SUBJECT 
SET SUB_NOW_STUDENT = SUB_NOW_STUDENT - 1
WHERE SUB_SEQ=#{sub_seq}

UPDATE ASSESSMENT
SET STUDENT_SCORE = #{student_score}
WHERE SUB_SEQ_NUM = #{sub_seq_num} AND STUDENT_ID = #{student_id}
