mapper.insert:
INSERT INTO Posts(
	board_seq, REF, POST_SEQ, USER_ID, TITLE, 
	CONTENT, STORED_FILENAME, ORG_FILENAME
)
VALUES(
	#{boardSeq}, POSTS_SEQ.NEXTVAL, POSTS_SEQ.NEXTVAL, #{userId}, #{bbsTitle}, 
	#{bbsContent}, #{bbsStoredFileName}, #{bbsOrgFileName}
)

mapper.update:
UPDATE POSTS
SET TITLE = #{bbsTitle},
	CONTENT = #{bbsContent},
	STORED_FILENAME = #{bbsStoredFileName}, 
	ORG_FILENAME = #{bbsOrgFileName}
WHERE BOARD_SEQ = #{boardSeq}
AND POST_SEQ = #{bbsSeq}

UPDATE POSTS
SET STATUS = '삭제'
WHERE POST_SEQ = #{bbsSeq}
