mapper.insert:
INSERT INTO GOODS(
	seq, category, userId, userName, title, 
	content, price, address, phone
)
VALUES(
	MAIN_BBS_SEQ.NEXTVAL, #{category}, #{userId}, #{userName}, #{title}, 
	#{content}, #{price}, #{address}, #{phone}
)

mapper.update:
UPDATE MAIN_BBS
SET BBS_TITLE = #{bbsTitle},
	BBS_CONTENT = #{bbsContent},
	BBS_STORED_FILENAME = #{bbsStoredFileName}, 
	BBS_ORG_FILENAME = #{bbsOrgFileName}
WHERE BOARD_URL = #{boardUrl}
AND BBS_SEQ = #{bbsSeq}

UPDATE GOODS
SET status = '삭제'
WHERE seq = #{seq}
